
public class OperatorEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 2;
		int y = 5;
		
		System.out.println(x + 1);
		System.out.println(y + 10 - x++);
		System.out.println(++x);
		System.out.println(x++);
		System.out.println(x);
		
	}

}
